CRUMINA.Bootstrap=function(){$('[data-toggle="tooltip"], [rel="tooltip"]').tooltip(),$('[data-toggle="popover"]').popover(),$(".selectpicker").selectpicker();var t=$('input[name="datetimepicker"]');if(t.length){var o=moment().subtract(29,"days");t.daterangepicker({startDate:o,autoUpdateInput:!1,singleDatePicker:!0,showDropdowns:!0,locale:{format:"DD/MM/YYYY"}}),t.on("focus",function(){$(this).closest(".form-group").addClass("is-focused")}),t.on("apply.daterangepicker",function(t,o){$(this).val(o.startDate.format("DD/MM/YYYY")),$(this).closest(".form-group").addClass("is-focused")}),t.on("hide.daterangepicker",function(){""===$(this).val()&&$(this).closest(".form-group").removeClass("is-focused")})}},$(document).ready(function(){CRUMINA.Bootstrap()});